mapper.update:
UPDATE alcohol_free_reply
SET type_content=#{content}, type_replyDate=sysdate
WHERE board_no=#{board_no} AND free_reply_no=#{reply_no}

UPDATE alcohol_free_reply
SET free_like=free_like+1
WHERE board_no=#{board_no} AND free_reply_no=#{reply_no}

UPDATE alcohol_free_reply
SET free_hate=free_hate+1
WHERE board_no=#{board_no} AND free_reply_no=#{reply_no}
